/**
* アカウント情報変更モーダルOpen要求アクション名
*/
export const ACCOUNT_INFO_MODAL_OPEN = "ACCOUNT_INFO_MODAL_OPEN";

/**
* アカウント情報変更モーダルOpenのcreator
* @return {Object} アカウント情報変更モーダルOpenアクション
*/
export function openAccountInfoModal(name, pass){
  return {
    type:ACCOUNT_INFO_MODAL_OPEN
  };
}

/**
* アカウント情報変更モーダルClose要求アクション名
*/
export const ACCOUNT_INFO_MODAL_CLOSE = "ACCOUNT_INFO_MODAL_CLOSE";

/**
* アカウント情報変更モーダルCloseのcreator
* @return {Object} アカウント情報変更モーダルCloseアクション
*/
export function closeccountInfoModal(){
  return {
    type:ACCOUNT_INFO_MODAL_CLOSE
  };
}

/**
* アカウント情報変更要求アクション名
*/
export const CHANGE_ACCOUNT_INFO = "CHANGE_ACCOUNT_INFO";

/**
* アカウント情報変更のcreator
* @return {Object} アカウント情報変更モーダルCloseアクション
*/
export function changeAccountInfo(name, pass){
  return {
    type:CHANGE_ACCOUNT_INFO,
    payload:{
      nextname:name,
      nextpass:pass
    }
  };
}

/**
* アカウント情報テキストフィールド(表示名)変更要求アクション名
*/
export const CHANGE_ACCOUNT_INFO_FIELD = "CHANGE_ACCOUNT_INFO_FIELD";

/**
* アカウント情報テキストフィールドの変更のcreator
* @return {Object} アカウント情報変更フィールド変更アクション
*/
export function changeAccountInfoField(name, pass){
  return {
    type:CHANGE_ACCOUNT_INFO_FIELD,
    payload:{
      nextname:name,
      nextpass:pass
    }
  };
}

/**
* チームメンバーの状況を更新するアクション名
*/
export const UPDATE_MEMBER_STATUS = 'UPDATE_MEMBER_STATUS';

/**
* チームメンバーの状況を更新するアクションのcreator
* @param {Object} memberStatus メンバー状況
* @return {Object} UPDATE_MEMBER_STATUSアクション
*/
export function updateMemberStatus(memberStatus){
  return {
    type:UPDATE_MEMBER_STATUS,
    payload:memberStatus
  }
}

export const CLEAR_MY_DESTINATION='CLEAR_MY_DESTINATION';

/**
* ログイン要求アクション名
*/
export const LOGIN_REQUESTED = "LOGIN_REQUESTED";

/**
* ログイン要求アクションのcreator
* @param {string} id ユーザーID
* @param {string} pass パスワード
* @return {Object} ログイン要求アクション
*/
export function loginRequested(id,pass){
  return {
    type:LOGIN_REQUESTED,
    payload:{
      id:id,
      pass:pass
    }
  };
}

/**
* ログイン成功アクション名
*/
export const LOGIN_SUCCESS = "LOGIN_SUCCESS";

/**
* ログイン成功アクションのcreator
* @param {{userid:string,name:string,token:string}} userinfo 成功したユーザー情報
* @return {Object} ログイン成功アクション
*/
export function loginSuccess(userinfo){
  return {
    type:LOGIN_SUCCESS,
    payload:userinfo
  };
}

/**
* ログイン失敗アクション名
*/
export const LOGIN_FAILURE = "LOGIN_FAILURE";

/**
* ログイン失敗アクションのcreator
* @return {Object} ログイン失敗アクション
*/
export function loginFailure(){
  return {
    type: LOGIN_FAILURE
  };
}

/**
* ログアウト要求アクション名
*/
export const LOGOUT_REQUESTED = "LOGOUT_REQUESTED";

/**
* ログアウト要求アクションのcreator
* @return {Object} ログアウト要求アクション
*/
export function logoutRequested(){
  return {
    type : LOGOUT_REQUESTED
  };
}

/**
* 自分の行き先変更アクション名
*/
export const MY_DESTINATION_CHANGE = "MY_DESTINATION_CHANGE";

/**
* 自分の行き先変更アクションのcreator
* @param {{inBusiness:boolean,comment:string,contact:string}} destination 行き先
* @return {Object} 自分の行き先変更アクション
*/
export function myDestinationChange(destination){
  return {
    type:MY_DESTINATION_CHANGE,
    payload:destination
  };
}

/**
* 自分の行き先クリアアクション名
*/
export const MY_DESTINATION_CLEAR = "MY_DESTINATION_CLEAR";

/**
* 自分の行き先クリアアクションのcreator
* @return {Object} 自分の行き先クリアアクション
*/
export function myDestinationClear(){
  return {
    type:MY_DESTINATION_CLEAR
  };
}

/**
*サインアップ要求アクション名
*/
export const SIGNUP_REQUIRED = "SIGNUP_REQUIRED";

/**
* サインアップ要求アクションのcreator
* @param {{id:string,password:string,name:string}} userinfo 登録を要求するユーザー情報
* @return {Object} SIGNUP_REQUIREDアクション
*/
export function signupRequired(userinfo){
  return {
    type:SIGNUP_REQUIRED,
    payload:userinfo
  }
}
/**
* サインアップ成功アクション名
*/
export const SIGNUP_SUCCESS = "SIGNUP_SUCCESS";
/**
* サインアップ成功アクションのcreator
* @return {Object} SIGNUP_SUCCESSアクション
*/
export function signupSucceess(){
  return {
    type:SIGNUP_SUCCESS
  };
}

/**
* サインアップ失敗アクション名
*/
export const SIGNUP_FAILURE = "SIGNUP_FAILURE";

/**
* サインアップ失敗アクションのcreator
* @param {array} reasons 失敗理由の配列
* @return {Object} SIGNUP_FAILUREアクション
*/
export function signupFailure(reasons){
  return {
    type:SIGNUP_FAILURE,
    payload:reasons
  }
}

/**
* 自分の行き先変更アクション名
*/
export const SUGGESTION_CHANGE = "SUGGESTION_CHANGE";

/**
* 自分の行き先変更アクションのcreator
* @param {{inBusiness:boolean,comment:string}} suggests 行き先
* @return {Object} 自分の行き先変更アクション
*/
export function suggestionChange(suggests){
  return {
    type:SUGGESTION_CHANGE,
    payload:suggests
  };
}

/**
* 自分の行き先クリアアクション名
*/
export const SUGGESTION_CLEAR = "SUGGESTION_CLEAR";

/**
* 自分の行き先クリアアクションのcreator
* @return {Object} 自分の行き先クリアアクション
*/
export function suggestionClear(){
  return {
    type:SUGGESTION_CLEAR
  };
}

import React from 'react';
import ReactDOM from 'react-dom';
import FlatButton from 'material-ui/FlatButton';
import TextField from 'material-ui/TextField';
import {Toolbar, ToolbarGroup, ToolbarSeparator, ToolbarTitle} from 'material-ui/Toolbar';
import Dialog from 'material-ui/Dialog';
import {closeccountInfoModal,changeAccountInfo,changeAccountInfoField} from '../action/accountboard';

/**
* AccountBoardコンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
export default class AccountBoard extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
    this.state = {pass_text:"",confirm_pass_text:"",name_text:""};
    //ES2015版のReactだとこのおまじないをしないとメソッド内でthisが解決しない...
    this.hundleClick = this.hundleClick.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.hundlePassChange = this.hundlePassChange.bind(this);
    this.hundleNameChange = this.hundleNameChange.bind(this);
  }

  /**
  * アカウント情報変更ボタンのクリックをハンドリングするメソッド
  * @return {undefined}
  */
  hundleClick(){
      this.props.dispatch(changeAccountInfo(this.props.accountboard.nextuser.nextname, this.props.accountboard.nextuser.nextpass));
  }

  /**
  * モーダルのクローズをハンドリングするメソッド
  * @return {undefined}
  */
  handleClose(){
      this.props.dispatch(closeccountInfoModal());
  }

  hundlePassChange(event,newValue){
    this.props.dispatch(changeAccountInfoField(this.props.accountboard.nextuser.nextname, newValue));
  }

  hundleNameChange(event,newValue){
    this.props.dispatch(changeAccountInfoField(newValue, this.props.accountboard.nextuser.nextpass));
  }

  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    const actions = [
      <FlatButton
        label="Cancel"
        primary={true}
        onTouchTap={this.handleClose}
      />,
      <FlatButton
        label="Submit"
        primary={true}
        onTouchTap={this.hundleClick}
      />,
    ];

    // ツールバーの表示
    const accountboard =
        <Dialog
          title="アカウント情報変更"
          actions={actions}
          modal={true}
          open={this.props.accountboard.open}
        >
          <TextField
          hintText="表示名"
          value={this.props.accountboard.nextuser.nextname}
          onChange={this.hundleNameChange}
          />
          <br />
          <TextField
          hintText="Password"
          value={this.props.accountboard.nextuser.nextpass}
          type="password"
          onChange={this.hundlePassChange}
          />
        </Dialog>

    return (
      <div>{accountboard}</div>
    )
  }
}

import React from 'react'
import ReactDOM from 'react-dom';
import {List, ListItem} from 'material-ui/List';
import Avatar from 'material-ui/Avatar';
import TextField from 'material-ui/TextField';
import {
  red600,
  grey400
} from 'material-ui/styles/colors';

/**
* 行き先掲示板コンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
export default class Board extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
    this.state = {
      current_filter_text:""
    }
    this.hundleFilterChange = this.hundleFilterChange.bind(this);
  }


  /**
  * 描画メソッド
  * @return {undefined}
  */

  hundleFilterChange(event,newValue){
//    const line = this.props.memberStatus;
//    const line = this.props.memberStatus.filter((name) => (
//      name.toLowerCase().indexOf(newValue) >= 0
//    ));
//
    this.setState({current_filter_text : newValue});
  }

  render(){
    //チームの数だけTeamBoardコンポーネントを作成する
    //const teamBoards = this.props.memberStatus.map(team => <TeamBoard key={team.teamId} team={team}/>);
    //const memberRows = this.state.filterdMemberStatus.map((member,idx) => <MemberRow key={idx} member={member}/>);

    const filterMemberRows = this.props.memberStatus.filter((member) => (
      member.name.toLowerCase().indexOf(this.state.current_filter_text) >= 0
    ));

    const memberRows = filterMemberRows.map((member,idx) => <MemberRow key={idx} member={member}/>);
    const date = new Date().toLocaleDateString('ja-JP');

    return (
      <div>
      <h3>行き先掲示板</h3>
      <p>{date}</p>
      <TextField
        hintText="Filter"
        value={this.state.current_filter_text}
        onChange={this.hundleFilterChange}
        />
      {memberRows}
      </div>);
  }
}
/**
* チーム別行き先掲示板コンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
class TeamBoard extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
  }
  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    //メンバーの数だけMemberRowコンポーネント作成する
//    const memberRows = this.props.team.members.map((member,idx) => <MemberRow key={idx} member={member}/>);
    return (
      <div>
        <h4>{this.props.team.name}</h4>
        <table>
        <tbody>
        {memberRows}
        </tbody>
        </table>
      </div>
    );
