}
/**
* 描画メソッド
* @return {undefined}
*/
class MemberRow extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
  }
  /**
  * 描画メソッド
  * @return {undefined}
  {this.props.member.name}
  <td>{this.props.member.name}</td>
  <td>{this.props.member.inBusiness ? "出勤" : "退勤"}</td>
  <td>{this.props.member.comment}</td>
  leftAvatar={<Avatar>{this.props.member.name.charAt(0).toUpperCase()}</Avatar>}
  */
  render(){
      //const memberRows = this.props.memberStatus.map((member,idx) => <MemberRow key={idx} member={member}/>);
    return (
      <List>
        <ListItem
          primaryText={this.props.member.name + "：" + this.props.member.contact}
            secondaryText={"最終更新日:" + this.props.member.lastUpdate.substr(0,16)+" "+(this.props.member.comment == null ? "" : this.props.member.comment)}
          leftAvatar={<Avatar backgroundColor={this.props.member.inBusiness ? red600 : grey400} > {this.props.member.inBusiness ? "出" : "退"}</Avatar>} />
      </List>
    );
  }
}

import React from 'react'
import {LOGIN_STATUS} from '../const/login'
import {logoutRequested} from '../action/login'
import Avatar from 'material-ui/Avatar';
import Chip from 'material-ui/Chip';
import IconMenu from 'material-ui/IconMenu';
import IconButton from 'material-ui/IconButton';
import NavigationExpandMoreIcon from 'material-ui/svg-icons/navigation/expand-more';
import MenuItem from 'material-ui/MenuItem';
import DropDownMenu from 'material-ui/DropDownMenu';
import FlatButton from 'material-ui/FlatButton';
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton';
import {Toolbar, ToolbarGroup, ToolbarSeparator, ToolbarTitle} from 'material-ui/Toolbar';
import Dialog from 'material-ui/Dialog';
import AccountBoard from './accountboard';
import {openAccountInfoModal} from '../action/accountboard'

/**
* Headerコンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
export default class Header extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
    //ES2015版のReactだとこのおまじないをしないとメソッド内でthisが解決しない...
    this.state = {open: false}
    this.hundleClick = this.hundleClick.bind(this);
    this.handleOpen = this.handleOpen.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  /**
  * ログアウトボタンのクリックをハンドリングするメソッド
  * @return {undefined}
  */
  hundleClick(){
      this.props.dispatch(logoutRequested());
  }

  /**
  * モーダルのオープンをハンドリングするメソッド
  * @return {undefined}
  */
  handleOpen(){
      this.props.dispatch(openAccountInfoModal());
  }

  /**
  * モーダルのクローズをハンドリングするメソッド
  * @return {undefined}
  */
  handleClose(){
      this.setState({open: false});
  }

  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    const actions = [
      <FlatButton
        label="Cancel"
        primary={true}
        onTouchTap={this.handleClose}
      />,
      <FlatButton
        label="Submit"
        primary={true}
        onTouchTap={this.handleClose}
      />,
    ];
    // ツールバーの表示
    const toolbar = this.props.login.status == LOGIN_STATUS.SUCCESS ?
      <Toolbar>
        <ToolbarGroup>
          <Chip><Avatar>{this.props.login.user.name.charAt(0).toUpperCase()}</Avatar>{this.props.login.user.name}</Chip>
          <ToolbarSeparator />
          <IconMenu
            iconButtonElement={
              <IconButton touch={true}>
                <NavigationExpandMoreIcon />
              </IconButton>
            }
          >
            <MenuItem primaryText="アカウント情報変更" onTouchTap={this.handleOpen} />
            <MenuItem primaryText="LOGOUT" onTouchTap={this.hundleClick} />
          </IconMenu>
        </ToolbarGroup>
        <AccountBoard dispatch={this.props.dispatch} accountboard={this.props.accountboard} />
      </Toolbar>:
      null

    return (
      <div><img src={"./img/logo.png"} />&nbsp;<br />{toolbar}</div>
    )
  }
}

import React from 'react'
import { connect } from 'react-redux'
import Header from './header'
import LoginForm from './loginform'
import SignUpForm from './signupform'
import Board from './board'
import MyDestination from './mydestination'
import {LOGIN_STATUS} from '../const/login'
import Paper from 'material-ui/Paper';


/**
*メインコンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
class Kyodoco extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
  }

  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    const style = {
      margin: 10,
      padding: 10,
      textAlign: 'center',
      display: 'inline-block',
    };

    //ログインしていればメイン画面を、そうでなければログインフォームを描画する
    const content =
      this.props.login.status == LOGIN_STATUS.SUCCESS ?
      (<div>
        <MyDestination
          dispatch={this.props.dispatch}
          login={this.props.login}
          mydestination={this.props.mydestination}
          suggestion={this.props.suggestion}
        />
        <Board
          memberStatus={this.props.board.memberStatus}
        />
       </div>
      ) :
      (
        <div>
          <Paper style={style} zDepth={2}>
            <LoginForm
            dispatch={this.props.dispatch}
            login={this.props.login}/>
          </Paper>
          <br />
          <Paper style={style} zDepth={2}>
            <SignUpForm
            dispatch={this.props.dispatch}
            signup={this.props.signup}/>
          </Paper>
        </div>
      );

    return(
      <div>
      <header>
        <Header dispatch={this.props.dispatch} login={this.props.login} accountboard={this.props.accountboard}/>
      </header>
      <hr />
      {content}
      <hr />
      copyright NST i-lab All Rights Reserved.
      </div>
    );
  }
}
/**
* reduxのstore上のstateをcomponentに接続する関数
* @see http://qiita.com/kompiro/items/7fe90c4abc92fd32b343
*/
function mapStateToProp(state){
  return state;
}

/**
* メインコンポーネントをstoreと接続してexportする
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
* @see http://qiita.com/kompiro/items/7fe90c4abc92fd32b343
*/
export default connect(mapStateToProp)(Kyodoco);

import React from 'react'
import ReactDOM from 'react-dom'
import {loginRequested} from '../action/login'
import {LOGIN_STATUS} from '../const/login'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'

/**
* LoginFormコンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
export default class LoginForm extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
    this.state = {current_id_text:"",current_pass_text:""};
    //ES2015版のReactだとこのおまじないをしないとメソッド内でthisが解決しない...
    this.hundleSubmit = this.hundleSubmit.bind(this);
    this.hundleIDChange = this.hundleIDChange.bind(this);
    this.hundlePassChange = this.hundlePassChange.bind(this);
  }

  /**
  * ログインボタンのクリックをハンドリングするメソッド
  * @return {undefined}
  */
  hundleSubmit(){
    /*
    *入力されたID/Passを取得して、
    *それを元にLOGIN_REQUESTED(ログイン要求)アクションを作成してdispatchする
    */
    const userid = this.state.current_id_text.trim();
    const password = this.state.current_pass_text.trim();
    if(userid && password){
      this.props.dispatch(loginRequested(userid,password));
    }
  }

  hundleIDChange(event,newValue){
    this.setState({current_id_text:newValue});
  }

  hundlePassChange(event,newValue){
    this.setState({current_pass_text:newValue});
  }

  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    //現在の状態がLOGIN_STATUS.FAILURE(ログイン失敗)であれば失敗メッセージを表示する
    const message = this.props.login.status == LOGIN_STATUS.FAILURE ?
      "IDもしくはパスワードが正しくありません" :
      "";

    return (
      <div>
        <h4>ログイン</h4>
        <TextField
        hintText="User ID"
        floatingLabelText="User ID"
        value={this.state.current_id_text}
        ref="userid"
        onChange={this.hundleIDChange}
        />
        <br />
        <TextField
          hintText="Password"
          floatingLabelText="Password"
          type="password"
          value={this.state.current_pass_text}
          ref="password"
          onChange={this.hundlePassChange}
          />
        <br />
        <br />
        <RaisedButton onClick={this.hundleSubmit} label="Login" ref="loginbutton"/>
        <br />
        <span style={{color:"red"}}>{message}</span>
      </div>
    );
  }
}

import React from 'react'
import ReactDOM from 'react-dom'
import {myDestinationChange,myDestinationClear} from '../action/mydestination'
import Toggle from 'material-ui/Toggle'
import TextField from 'material-ui/TextField'
import AutoComplete from 'material-ui/AutoComplete';
import RaisedButton from 'material-ui/RaisedButton';

/**
* "自分の行き先"コンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
export default class MyDestination extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props
  * @return {undefined}
  */
  constructor(props){
    super(props);

    //ES2015版のReactだとこのおまじないをしないとメソッド内でthisが解決しない...
    this.hundleClear = this.hundleClear.bind(this);
    this.hundleToggle = this.hundleToggle.bind(this);
    this.hundleUpdateInput = this.hundleUpdateInput.bind(this);
    this.hundleContactChange = this.hundleContactChange.bind(this);
  }

  hundleClear(){
    const action = myDestinationClear();
    this.props.dispatch(action);
  }

  hundleToggle(event,isInputChecked){
    this.props.dispatch(myDestinationChange({
        inBusiness:isInputChecked, comment:this.props.mydestination.comment, contact:this.props.mydestination.contact
    }));
  }

  hundleUpdateInput(newValue) {
    this.props.dispatch(myDestinationChange({
