import {take,put,select,takeEvery,takeLatest,call} from 'redux-saga/effects'
import {LOGIN_REQUESTED,LOGIN_FAILURE,LOGOUT_REQUESTED,loginRequested,loginSuccess,loginFailure} from '../action/login'
import {CHANGE_ACCOUNT_INFO} from '../action/accountboard'
import {getFromStorage,setToStorage,removeFromStorage,existsKeyOnStorage} from '../module/localstorage'
import axios from "axios";

/**
* アカウント情報変更要求を受け付けるSaga
* @see http://qiita.com/kuy/items/716affc808ebb3e1e8ac
*/
export function* changeAccountInfoSaga(){
  /*
  * CHANGE_ACCOUNT_INFO(アカウント情報変更要求)アクションを常に待ち続け、
  * dispatchされたらchangeAccountInfoTaskにactionを渡して実行する
  */
  yield takeEvery(CHANGE_ACCOUNT_INFO,changeAccountInfoTask);
}
/**
* APIに対してID/Passを問い合わせて、
* 成功すればユーザー情報を元にLOGIN_SUCCESS(ログイン成功)アクションをput(dispatch)する
* @param {object} action LOGIN_REQUESTED(ログイン要求)アクション
*/
function* changeAccountInfoTask(action){

  const changeData = {};
  if(action.payload.nextpass){
    changeData.newPassword = action.payload.nextpass;
  }
  if(action.payload.nextname){
    changeData.name = action.payload.nextname;
  }


  const id = yield select(state => state.login.user.userid);

  //XXX 現在の実装ではサーバエラーなのかID/Passが間違っているのか判断がつかない
  try{
    const token = yield select(state => state.login.user.token);
    const result = yield call(axios, {
      method:"PUT",
      url:"https://api.kyo-do.co/user",
      headers:{"Authorization":"Bearer " + token},
      data: changeData
    });
    const name = (result.data.name)?
      result.data.name:
      yield select(state => state.login.user.name);
    const id = yield select(state => state.login.user.userid);

    yield put(loginSuccess({userid:id,name:name,token:token}));
  }catch(e){
    console.log('error');
    //yield put(loginFailure());
  }
}

import {take,put,select,takeEvery,call} from 'redux-saga/effects'
import {delay} from 'redux-saga'
import {updateMemberStatus} from '../action/board'
import {LOGIN_SUCCESS} from '../action/login'
import {LOGIN_STATUS} from '../const/login'
import {MY_DESTINATION_CHANGE,MY_DESTINATION_CLEAR} from '../action/mydestination'
import axios from "axios";
import {SUGGESTION_CHANGE,SUGGESTION_CLEAR} from '../action/suggestion'

/**
* メンバー状況をpollingする間隔(ms)
*/
const STATUS_POLLING_DURATION_MS= 15000;

/**
* メンバー状況をロードするSaga
* @see http://qiita.com/kuy/items/716affc808ebb3e1e8ac
*/
export function* loadMemberStatusSaga(){
  yield takeEvery(LOGIN_SUCCESS,loadMemberStatusTask);
  yield takeEvery(MY_DESTINATION_CHANGE,loadMemberStatusTask);
  yield takeEvery(MY_DESTINATION_CLEAR,loadMemberStatusTask);
  yield takeEvery(SUGGESTION_CHANGE,loadMemberStatusTask);
  yield takeEvery(SUGGESTION_CLEAR,loadMemberStatusTask);
}

/**
* メンバー状況をロードするTask
*/
function* loadMemberStatusTask(){
  //TODO ダミー実装。本来はAPIから取得
  const me = yield select(state => state.login.user);
  const mydestination = yield select(state => state.mydestination);
  const memberStatus = yield getMemberStatus(me,mydestination);
  yield put(updateMemberStatus(memberStatus));
}

/**
* メンバー状況を定期的に確認するSaga
* @see http://qiita.com/kuy/items/716affc808ebb3e1e8ac
*/
export function* watchMemberStatusSaga(){
  while(true){
    const loginStatus = yield select(state => state.login.status);
    if(loginStatus == LOGIN_STATUS.SUCCESS){
      //TODO ダミー実装。本来はAPIから取得
      const me = yield select(state => state.login.user);
      const mydestination = yield select(state => state.mydestination);
      const memberStatus = yield getMemberStatus(me,mydestination);
      yield put(updateMemberStatus(memberStatus));
    }
    yield call(delay,STATUS_POLLING_DURATION_MS);
  }
}

/**
* メンバーの状況を返す
* @param {Object} me 自分のユーザー情報
* @param {Object} mydestination 自分の行き先
* @return {Object} メンバー状況
*/
function* getMemberStatus(me,mydestination){
  const token = yield select(state => state.login.user.token);
  try{
    const result = yield call(axios,{
      method:"GET",
      url:"https://api.kyo-do.co/status/all",
      headers: { "Authorization": "Bearer " + token}
    });

    // 自分の情報を先頭に表示する
    const memberStatus = result.data.filter(function(value, index, array){ return value.userid != me.userid });
    return result.data.filter(function(value, index, array){ return value.userid == me.userid }).concat(memberStatus);
  }catch(e){
    //XXX　エラーが起きればID重複と判断しているが、サーバエラーと区別したい
  }

}

import {take,put,call,takeEvery} from 'redux-saga/effects'
import {LOGIN_REQUESTED,LOGIN_FAILURE,LOGOUT_REQUESTED,loginRequested,loginSuccess,loginFailure} from '../action/login'
import {getFromStorage,setToStorage,removeFromStorage,existsKeyOnStorage} from '../module/localstorage'
import {getApiBaseURL} from '../module/environment';
import axios from "axios";

/**
* rememberme情報（入力されたID/Pass）をlocalStorageに保存する際のkey
*/
const REMEMBER_ME_STORAGE_KEY = "rememberme";
/**
* APIのベースURL
*/
const BASE_API_URL = getApiBaseURL();

/**
* login要求を受け付けるSaga
* @see http://qiita.com/kuy/items/716affc808ebb3e1e8ac
*/
export function* loginSaga(){
  /*
  * LOGIN_REQUESTED(ログイン要求)アクションを常に待ち続け、
  * dispatchされたらloginTaskにactionを渡して実行する
  */
  yield takeEvery(LOGIN_REQUESTED,loginTask);
}
/**
* APIに対してID/Passを問い合わせて、
* 成功すればユーザー情報を元にLOGIN_SUCCESS(ログイン成功)アクションをput(dispatch)する
* @param {object} action LOGIN_REQUESTED(ログイン要求)アクション
*/
export function* loginTask(action){
  //rememberme
  try{
    yield setToStorage(REMEMBER_ME_STORAGE_KEY,{id:action.payload.id,pass:action.payload.pass});
  }catch(e){
    //localStorageにアクセスできない場合は何もしない
  }


  //XXX 現在の実装ではサーバエラーなのかID/Passが間違っているのか判断がつかない
  try{
    const result = yield call(axios.post,BASE_API_URL + "auth",{
      userid:action.payload.id,
      password:action.payload.pass
    });

    const token = result.data.token;

    const resule_user = yield call(axios,{
      method:"GET",
      url:BASE_API_URL + "user",
      headers:{"Authorization":"Bearer " + token}
    });

    yield put(loginSuccess({userid:action.payload.id,name:resule_user.data.name,token:token}));
  }catch(e){
    yield put(loginFailure());
  }
}
/**
* logout要求を受けるSaga
* @see http://qiita.com/kuy/items/716affc808ebb3e1e8ac
*/
export function* logoutSaga(){
  yield takeEvery(LOGOUT_REQUESTED,cleanRememberMeTask);
}

/**
* ログインに失敗したときのクリーンアップを行うSaga
* @see http://qiita.com/kuy/items/716affc808ebb3e1e8ac
*/
export function* loginFailureSaga(){
  yield takeEvery(LOGIN_FAILURE,cleanRememberMeTask);
}
/**
* localStorageに保存されているrememberme情報をクリアするTask
*/
export function* cleanRememberMeTask(){
  try{
    yield removeFromStorage(REMEMBER_ME_STORAGE_KEY);
  }catch(e){
    //localStorageにアクセスできない場合は何もしない
  }

}

/**
* localStorageに保存されているID/PassからログインするSaga
* アプリを起動して(ブラウザアクセスして)1回だけ実行される
* @see http://qiita.com/kuy/items/716affc808ebb3e1e8ac
*/
export function* loginFromRememberMeSaga(){
  try{
    const remembermeSaved = yield existsKeyOnStorage(REMEMBER_ME_STORAGE_KEY);
    if(remembermeSaved){
      const rememberme = yield getFromStorage(REMEMBER_ME_STORAGE_KEY);
      yield put(loginRequested(rememberme.id,rememberme.pass));
    }
  }catch(e){
    //localStorageにアクセスできない場合は何もしない
  }
}

